//..........................................................
// 变换矩阵声明
uniform vec4 vc_land;
uniform vec4 vc_ocean;
uniform mat4 vc_model_matrix;
uniform mat4 vc_vp_matrix;
//..........................................................
// 属性流声明
attribute vec3 va_position;
attribute vec4 va_color;
attribute vec2 va_coord;
//..........................................................
// 属性流声明
varying vec4 v_color;
varying vec2 v_coord;
//..........................................................
// 处理
void main(){
   // 计算模型坐标
   vec4 position = vec4(va_position, 1.0);
   // 计算最终坐标
   gl_Position = position;
   // 设置颜色
   //v_color = va_color * vc_data[index + 3];
   // 设置纹理
   v_coord = vec2(va_coord.x, 1.0 - va_coord.y);
}

// 声明环境
#ifdef GL_ES
precision mediump float;
#endif
//..........................................................
// 声明常量
uniform vec4 fc_cloud;
uniform vec4 fc_land;
uniform vec4 fc_ocean;
uniform vec4 fc_water;
//..........................................................
// 声明插值器
varying vec4 v_color;
varying vec2 v_coord;
//..........................................................
// 声明取样器
uniform sampler2D fs_cloud;
uniform sampler2D fs_land;
uniform sampler2D fs_ocean;
uniform sampler2D fs_water;
uniform sampler2D fs_water_normal;
//..........................................................
// 处理
void main(void){
   vec3 constGrey = vec3(0.222, 0.707, 0.071);
   // 获得云层颜色
   vec2 coordCloud = vec2(v_coord.x + fc_cloud.x, v_coord.y);
   vec4 colorCloud = texture2D(fs_cloud, coordCloud);
   float rateCloud = dot(colorCloud.rgb, constGrey.rgb) * 0.6;
   // 获得陆地颜色
   vec4 colorLand = texture2D(fs_land, v_coord);
   // 获得水纹颜色
   vec2 coordWater = vec2(v_coord.x + fc_water.x, v_coord.y) * 30.0;
   vec4 colorWater = texture2D(fs_water, coordWater);
   vec4 colorWaterNormal = texture2D(fs_water_normal, coordWater);
   vec3 waterNormal = normalize(colorWaterNormal.xyz) * colorWaterNormal.w;
   float rateWater = dot(colorWater.rgb, constGrey.rgb) * 0.3;
   // 获得海洋颜色
   vec4 colorOcean = texture2D(fs_ocean, v_coord + waterNormal.xy * 0.01);
   //..........................................................
   // 合成颜色
   float rate = 1.0;
   vec4 result = vec4(0.0, 0.0, 0.0, 1.0);
   // 合并云颜色
   result.rgb += colorCloud.rgb * rateCloud;
   rate = max(rate - rateCloud, 0.0);
   // 合并陆地颜色
   result.rgb += colorLand.rgb * rate * colorLand.w;
   rate = max(rate - colorLand.w, 0.0);
   // 合并水纹颜色
   result.rgb += colorWater.rgb * rate * rateWater;
   rate = max(rate - rateWater, 0.0);
   // 合并海洋颜色
   result.rgb += colorOcean.rgb * rate;
   gl_FragColor = result;
   //gl_FragColor = vec4(rateCloud, rateCloud, rateCloud, 1.0);
   //gl_FragColor = vec4(rateWater, rateWater, rateWater, 1.0);
   //gl_FragColor = colorLand;
}
